import 'package:equatable/equatable.dart';import 'package:flutter/material.dart';import '/core/app_export.dart';import '../models/notificationlist_item_model.dart';import 'package:drfarama/presentation/brand_infotwo_screen/models/brand_infotwo_model.dart';part 'brand_infotwo_event.dart';part 'brand_infotwo_state.dart';/// A bloc that manages the state of a BrandInfotwo according to the event that is dispatched to it.
class BrandInfotwoBloc extends Bloc<BrandInfotwoEvent, BrandInfotwoState> {BrandInfotwoBloc(BrandInfotwoState initialState) : super(initialState) { on<BrandInfotwoInitialEvent>(_onInitialize); }

_onInitialize(BrandInfotwoInitialEvent event, Emitter<BrandInfotwoState> emit, ) async  { emit(state.copyWith(brandInfotwoModelObj: state.brandInfotwoModelObj?.copyWith(notificationlistItemList: fillNotificationlistItemList()))); } 
List<NotificationlistItemModel> fillNotificationlistItemList() { return [NotificationlistItemModel(notificationImage: ImageConstant.imgTelevision, notificationText1: "1000 riyals have been deposited in your wallet", notificationText2: "30 may 2023 - 6:55 PM", notificationText3: "+1000\nEGP"), NotificationlistItemModel(notificationImage: ImageConstant.imgTelevisionRedA200, notificationText1: "1000 riyals were paid in an invoice", notificationText2: "30 may 2023 - 6:55 PM", notificationText3: "-1000 EGP"), NotificationlistItemModel(notificationImage: ImageConstant.imgTelevision, notificationText1: "1000 riyals have been deposited in your wallet", notificationText2: "30 may 2023 - 6:55 PM", notificationText3: "+1000\nEGP")]; } 
 }
